DROP TABLE IF EXISTS `__PREFIX__free_message`;
CREATE TABLE `__PREFIX__free_message` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `launch_id` int(11) NOT NULL COMMENT '发起人ID',
  `user_id` int(11) NOT NULL COMMENT '接收人ID',
  `title` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '0' COMMENT '标题',
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '内容',
  `img` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT '0' COMMENT '图片',
  `avatar_url` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '头像跳转地址',
  `content_url` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '内容跳转地址',
  `read` tinyint(1) unsigned zerofill NOT NULL DEFAULT '0' COMMENT '读',
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '类型',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',
  `weigh` int(11) NOT NULL DEFAULT '0' COMMENT '权重',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `_user_id` (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='消息表';

