-- ----------------------------
-- Table structure for __PREFIX__permissions
-- ----------------------------
DROP TABLE IF EXISTS `__PREFIX__permissions`;
CREATE TABLE `__PREFIX__permissions` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) NOT NULL DEFAULT '0' COMMENT '上级ID',
  `permission_mark` varchar(191) NOT NULL DEFAULT '' COMMENT '权限标识',
  `permission_name` varchar(255) NOT NULL DEFAULT '' COMMENT '菜单名称',
  `type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '类型:0:目录，菜单，按钮',
  `icon` varchar(255) DEFAULT NULL COMMENT '图标',
  `params` varchar(255) DEFAULT NULL COMMENT '参数',
  `route` varchar(255) NOT NULL DEFAULT '' COMMENT '前端路由',
  `component` varchar(255) DEFAULT NULL COMMENT '组件',
  `redirect` varchar(255) DEFAULT NULL COMMENT '跳转地址',
  `weigh` int(11) NOT NULL DEFAULT '0' COMMENT '权重',
  `status` enum('show','hidden','disabled') NOT NULL DEFAULT 'show' COMMENT '状态',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `permission_name` (`permission_name`)
) ENGINE=InnoDB AUTO_INCREMENT=740 DEFAULT CHARSET=utf8mb4 COMMENT='菜单权限';

